import { Link } from 'react-router-dom';

export default function ProductCard({ product }) {
  return (
    <div className="bg-white p-4 rounded shadow hover:shadow-md transition">
      <img src={product.image} alt={product.title} className="h-40 object-contain mx-auto" />
      <h2 className="text-lg font-semibold mt-3 truncate">{product.title}</h2>
      <p className="text-green-600 font-medium mt-1">${product.price}</p>
      <p className="text-yellow-500 text-sm">‚≠ê {product.rating?.rate} ({product.rating?.count})</p>
      <a href={`/product/${product.id}`} className="text-blue-500 mt-2 inline-block">View Details</a>
    </div>
  );
}
