import { Link } from 'react-router-dom';

export default function ProductCard({ product }) {
  return (
    <div className="bg-white p-4 rounded shadow hover:shadow-md transition hover:ring-1 hover:ring-blue-300">
      <Link to={`/product/${product.id}`} aria-label={`View ${product.title}`}>
        <img
          src={product.image}
          alt={product.title}
          className="h-40 object-contain mx-auto"
        />
        <h2 className="text-lg font-semibold mt-3 truncate">{product.title}</h2>
        <p className="text-green-600 font-medium mt-1">${product.price}</p>
        <p className="text-yellow-500 text-sm">
          ‚≠ê {product.rating?.rate} ({product.rating?.count})
        </p>
      </Link>

      {/* View Details Button */}
      <div className="mt-4">
        <Link
          to={`/product/${product.id}`}
          className="inline-block px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
        >
          View Details
        </Link>
      </div>
    </div>
  );
}
