import { Link } from 'react-router-dom';

export default function ProductCard({ product }) {
  return (
    <Link to={`/product/${product.id}`}>
      <div className="bg-white p-4 rounded shadow hover:shadow-md transition hover:ring-1 hover:ring-blue-300 cursor-pointer">
        <img src={product.image} alt={product.title} className="h-40 object-contain mx-auto" />
        <h2 className="text-lg font-semibold mt-3 truncate">{product.title}</h2>
        <p className="text-green-600 font-medium mt-1">${product.price}</p>
        <p className="text-yellow-500 text-sm">⭐ {product.rating?.rate} ({product.rating?.count})</p>
        {/* Remove View Details link — card itself is now clickable */}
      </div>
    </Link>
  );
}
